import React, { CSSProperties } from 'react';

// Modern blue-black styling constants that you can reuse across all components
const styles: { [key: string]: CSSProperties } = {
  section: {
    background: 'linear-gradient(135deg, rgba(15, 23, 42, 0.95) 0%, rgba(30, 41, 59, 0.95) 100%)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.4), 0 0 0 1px rgba(59, 130, 246, 0.3)',
    padding: '4rem 3rem',
    margin: '2rem auto',
    maxWidth: '800px',
    position: 'relative' as const,
    overflow: 'hidden' as const,
    border: '1px solid rgba(59, 130, 246, 0.2)',
    transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
    transform: 'translateY(0)',
  },

  sectionHover: {
    transform: 'translateY(-8px)',
    boxShadow: '0 35px 60px -12px rgba(0, 0, 0, 0.6), 0 0 0 1px rgba(59, 130, 246, 0.5)',
  },

  title: {
    background: 'linear-gradient(135deg, #3b82f6 0%, #1d4ed8 50%, #1e40af 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    backgroundClip: 'text',
    fontSize: 'clamp(2.5rem, 5vw, 3.5rem)',
    fontWeight: '800' as const,
    marginBottom: '2rem',
    textAlign: 'center' as const,
    letterSpacing: '-0.02em',
    position: 'relative' as const,
  },

  content: {
    color: '#cbd5e1',
    fontSize: 'clamp(1.1rem, 2.5vw, 1.3rem)',
    lineHeight: '1.8',
    textAlign: 'center' as const,
    fontWeight: '400' as const,
    maxWidth: '600px',
    margin: '0 auto',
    position: 'relative' as const,
    zIndex: 2,
  },

  decorativeElement: {
    position: 'absolute' as const,
    top: '-50%',
    right: '-50%',
    width: '200%',
    height: '200%',
    background: 'radial-gradient(circle, rgba(59, 130, 246, 0.15) 0%, transparent 70%)',
    borderRadius: '50%',
    zIndex: 1,
    animation: 'float 6s ease-in-out infinite',
  },

  highlight: {
    background: 'linear-gradient(135deg, #3b82f6 0%, #60a5fa 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    backgroundClip: 'text',
    fontWeight: '600' as const,
  }
};

const About: React.FC = () => {
    const [isHovered, setIsHovered] = React.useState(false);

    return (
        <>
            <style>
                {`
                    @keyframes float {
                        0%, 100% { transform: translateY(0px) rotate(0deg); }
                        33% { transform: translateY(-10px) rotate(1deg); }
                        66% { transform: translateY(5px) rotate(-1deg); }
                    }

                    @keyframes fadeInUp {
                        from {
                            opacity: 0;
                            transform: translateY(30px);
                        }
                        to {
                            opacity: 1;
                            transform: translateY(0);
                        }
                    }

                    .fade-in-up {
                        animation: fadeInUp 0.8s ease-out forwards;
                    }
                `}
            </style>

            <section 
                id="about" 
                style={{
                    ...styles.section,
                    ...(isHovered ? styles.sectionHover : {})
                }}
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
                className="fade-in-up"
            >
                {/* Decorative background element */}
                <div style={styles.decorativeElement}></div>

                <h2 style={styles.title}>
                    About Me
                </h2>

                <p style={styles.content}>
                    I am <span style={styles.highlight}>Mohith</span>, a passionate software engineer with expertise in 
                    <span style={styles.highlight}> web development</span>, 
                    <span style={styles.highlight}> React</span>, and modern JavaScript frameworks. 
                    I graduated from <span style={styles.highlight}>SIT, ISE</span>, and have experience building 
                    scalable applications and working in collaborative teams.
                </p>
            </section>
        </>
    );
};

export default About;
