import React from 'react';

const Education: React.FC = () => (
    <section
        id="education"
        style={{
            background: "#f8fafc",
            borderRadius: "14px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.07)",
            padding: "32px",
            margin: "32px auto",
            maxWidth: "700px",
            boxSizing: "border-box"
        }}
    >
        <h2
            style={{
                fontSize: "2rem",
                color: "#1a237e",
                marginBottom: "24px",
                textAlign: "center",
                fontWeight: 700
            }}
        >
            Education
        </h2>
        <ul style={{ padding: 0, margin: 0, listStyle: "none" }}>
            <li style={{
                background: "#e3f2fd",
                borderRadius: "10px",
                padding: "16px",
                marginBottom: "16px",
                fontSize: "1.1rem",
                boxShadow: "0 2px 8px rgba(0,0,0,0.04)"
            }}>
                <strong>Siddaganga Institute of Technology (SIT), Tumkur</strong><br />
                B.E. in Information Science & Engineering<br />
                Year: 2022 - 2026
            </li>
            <li style={{
                background: "#e3f2fd",
                borderRadius: "10px",
                padding: "16px",
                marginBottom: "16px",
                fontSize: "1.1rem",
                boxShadow: "0 2px 8px rgba(0,0,0,0.04)"
            }}>
                <strong>KPS PU College, Arasikere</strong><br />
                Pre-University Education<br />
                Year: 2020 - 2022
            </li>
            <li style={{
                background: "#e3f2fd",
                borderRadius: "10px",
                padding: "16px",
                marginBottom: "0",
                fontSize: "1.1rem",
                boxShadow: "0 2px 8px rgba(0,0,0,0.04)"
            }}>
                <strong>Rotary High School</strong><br />
                High School Education<br />
                Year: 2020 (SSLC passed out)
            </li>
        </ul>
        {/* Responsive styling */}
        <style>
            {`
                @media (max-width: 600px) {
                    section#education {
                        padding: 16px !important;
                        margin: 16px auto !important;
                    }
                    section#education h2 {
                        font-size: 1.3rem !important;
                    }
                    section#education ul li {
                        font-size: 1rem !important;
                        padding: 10px !important;
                    }
                }
            `}
        </style>
    </section>
);

export default Education;
