import React from 'react';

const Contact: React.FC = () => {
    return (
        <section
            id="contact"
            style={{
                background: "#f9f9f9",
                borderRadius: "8px",
                boxShadow: "0 2px 8px rgba(0,0,0,0.07)",
                padding: "32px",
                margin: "24px auto",
                maxWidth: "700px",
                boxSizing: "border-box"
            }}
        >
            <h2
                style={{
                    color: "#333",
                    fontSize: "2rem",
                    marginBottom: "16px",
                    textAlign: "center"
                }}
            >
                Contact
            </h2>
            <p style={{
                color: "#444",
                fontSize: "1.1rem",
                marginBottom: "24px",
                textAlign: "center"
            }}>
                You can reach me at:<br />
                <strong>Email:</strong> mohith.1si22is053@gmail.com<br />
                <strong>Phone:</strong> +91-6361113046<br />
                <strong>LinkedIn:</strong>{" "}
                <a
                    href="https://www.linkedin.com/in/mohith-n-r-085a94263/"
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{ color: "#1976d2", textDecoration: "underline" }}
                >
                    linkedin.com/in/mohith
                </a>
            </p>
            <form style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
                <div style={{ display: "flex", flexDirection: "column", gap: "6px" }}>
                    <label htmlFor="name" style={{ fontWeight: 500, color: "#333" }}>Name:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        required
                        style={{
                            padding: "8px",
                            borderRadius: "6px",
                            border: "1px solid #ccc",
                            fontSize: "1rem"
                        }}
                    />
                </div>
                <div style={{ display: "flex", flexDirection: "column", gap: "6px" }}>
                    <label htmlFor="email" style={{ fontWeight: 500, color: "#333" }}>Email:</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        required
                        style={{
                            padding: "8px",
                            borderRadius: "6px",
                            border: "1px solid #ccc",
                            fontSize: "1rem"
                        }}
                    />
                </div>
                <div style={{ display: "flex", flexDirection: "column", gap: "6px" }}>
                    <label htmlFor="message" style={{ fontWeight: 500, color: "#333" }}>Message:</label>
                    <textarea
                        id="message"
                        name="message"
                        required
                        rows={4}
                        style={{
                            padding: "8px",
                            borderRadius: "6px",
                            border: "1px solid #ccc",
                            fontSize: "1rem",
                            resize: "vertical"
                        }}
                    ></textarea>
                </div>
                <button
                    type="submit"
                    style={{
                        background: "#1976d2",
                        color: "#fff",
                        border: "none",
                        borderRadius: "6px",
                        padding: "10px 0",
                        fontSize: "1.1rem",
                        fontWeight: 600,
                        cursor: "pointer",
                        transition: "background 0.2s"
                    }}
                    onMouseOver={e => (e.currentTarget.style.background = "#1565c0")}
                    onMouseOut={e => (e.currentTarget.style.background = "#1976d2")}
                >
                    Send
                </button>
            </form>
            {/* Responsive styling */}
            <style>
                {`
                    @media (max-width: 600px) {
                        section#contact {
                            padding: 16px !important;
                            margin: 12px auto !important;
                        }
                        section#contact h2 {
                            font-size: 1.3rem !important;
                        }
                        section#contact p {
                            font-size: 1rem !important;
                        }
                        section#contact form {
                            gap: 10px !important;
                        }
                    }
                `}
            </style>
        </section>
    );
};

export default Contact;
